import React from 'react';
import './ModernHomePage.css';
import TrendingStocks from './TrendingStocks';

const ModernHomePage = () => {
    const [bgOpacity, setBgOpacity] = useState(0);

    useEffect(() => {
        const handleScroll = () => {
            const scrollPosition = window.scrollY;
            const windowHeight = window.innerHeight;
            const maxScroll = windowHeight * 1.5; // Adjust as needed to control when the background fully darkens

            const newOpacity = Math.min(scrollPosition / maxScroll, 1);
            setBgOpacity(newOpacity);
        };

        window.addEventListener('scroll', handleScroll);
        return () => {
            window.removeEventListener('scroll', handleScroll);
        };
    }, []);


    return (
        <div>
            <div className="video-container">
                <video autoPlay muted loop>
                    <source src="/HomePageAnimation.mp4" type="video/mp4" />
                    Your browser does not support the video tag.
                </video>
            </div>
            <div className="content" style={{ backgroundColor: `rgba(0, 0, 0, ${bgOpacity})` }}>
                <h1>Welcome to the Page</h1>
                <p>Scroll down to see more content...</p>
                <p>
                    Lorem ipsum dolor sit amet, consectetur adipiscing elit. Sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat.
                </p>
                <p>
                    Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum.
                </p>
                <p>
                    More content here...
                </p>
                <p>
                    And even more content to scroll through...
                </p>
            </div>
            <TrendingStocks />
        </div>
    );
};

export default ModernHomePage;
