import React, { useEffect, useRef, useState } from 'react';
import './LoadUpPage.css';

const LoadUpPage = ({ onVideoEnd }) => {
    const videoRef = useRef(null);
    const [showButton, setShowButton] = useState(false);
    const [buttonClass, setButtonClass] = useState('enter-button');

    // Time (in seconds) when the button should start fading in
    const fadeInTime = 5; // Example: fade in at 5 seconds

    useEffect(() => {
        const handleVideoTimeUpdate = () => {
            const video = videoRef.current;
            if (video) {
                if (video.currentTime >= fadeInTime) {
                    setButtonClass('enter-button fade-in');
                }
            }
        };

        const handleVideoEnd = () => {
            setShowButton(true);
        };

        const resetVideo = () => {
            const video = videoRef.current;
            if (video) {
                video.currentTime = 0;
                video.play().catch(error => {
                    console.error("Video playback error:", error);
                });
            }
        };

        resetVideo();  // Reset video on mount

        const video = videoRef.current;
        if (video) {
            video.addEventListener('timeupdate', handleVideoTimeUpdate);
            video.addEventListener('ended', handleVideoEnd);
        }

        return () => {
            if (video) {
                video.removeEventListener('timeupdate', handleVideoTimeUpdate);
                video.removeEventListener('ended', handleVideoEnd);
            }
        };
    }, []);

    return (
        <div className="intro-video-container">
            <video
                ref={videoRef}
                src="/StartUpScreen.mov"
                autoPlay
                muted
                className="intro-video"
            />
            {showButton && (
                <button className={buttonClass} onClick={onVideoEnd}>
                    Enter
                </button>
            )}
        </div>
    );
};

export default LoadUpPage;
